mapper.select:
select aptname, no, dong, code, buildyear, jibun from housedeal where type in
#{arr}
and dong like concat('%', #{dong}, '%')
and AptName like concat('%', #{aptname}, '%')
and dong like concat('%', #{dealtype}, '%') or AptName like concat('%', #{dealtype}, '%')
group by aptname limit #{start}, #{interval}

select no, dong, aptName, code, buildYear, jibun, lat, lng, img
from houseinfo
where no=#{no}
