mapper.select:
select user_no as userNo, id, password, name, tel, address
from user
where id=#{id} and password=#{password}

select user_no as userNo, id, password, name, tel, address
from user
where id=#{id}

mapper.update:
update user
set password=#{password}, name=#{name}, tel=#{tel}, address=#{address}
where id=#{id}
